import UIKit

protocol ProductListPresentationLogic {
    func presentFetchedProducts(response: ProductList.Response)
}

class ProductListPresenter: ProductListPresentationLogic {
    weak var viewController: ProductListDisplayLogic?
  
    // MARK: - Present Fetched Products
  
    func presentFetchedProducts(response: ProductList.Response) {
        guard let products = response.productModel else {
            guard let error = response.error else { return }
            viewController?.displayError(error: error)
            return
        }
        let productListViewModel = ProductListViewModel(resultModel: products)
        let viewModel = ProductList.ViewModel(displayResults: productListViewModel)
        viewController?.displayProductList(viewModel: viewModel)
    }
}
