import UIKit

class ProductDetailWorker {
    func productDetailService(productId: String, completion: @escaping((ProductDetailModel?, APIError?) -> Void)) {
        ProductsService.shared.productDetailServiceCall(productId: productId) { (product, error) in
            completion(product, error)
        }
    }
    
    func submitReview(productId: String, rating: Int, review: String, completion: @escaping((ReviewModel?, APIError?) -> Void)) {
        let json: [String: Any] = ["id": "\(productId)",
                                   "locale": "en",
                                   "rating": rating,
                                   "text": "\(review)"]
        ReviewServices.shared.submitReview(productId: productId, reviewModel: json) { (result, error) in
            completion(result, error)
        }
    }
}
