@testable import AdiChallenge
import XCTest

class ProductDetailWorkerTests: XCTestCase
{
  // MARK: Subject under test
  
  var sut: ProductDetailWorker!
  
  // MARK: Test lifecycle
  
  override func setUp()
  {
    super.setUp()
    setupProductDetailWorker()
  }
  
  override func tearDown()
  {
    super.tearDown()
  }
  
  // MARK: Test setup
  
  func setupProductDetailWorker()
  {
    sut = ProductDetailWorker()
  }
  
  // MARK: Test doubles
  
  // MARK: Tests
  
    func testProductDetailService() {
    let expectation = self.expectation(description: "Wait for productDetailService() to return.")
        let productId: String = "HI333"
        sut.productDetailService(productId: productId) { (product, error) in
            XCTAssert(error == nil, "Api Call Failed")
            expectation.fulfill()
        }
    waitForExpectations(timeout: 60)
  }
}
